import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import Loader from '../common/Loader';
import Menu from '../innerpage/Menu';
import Footer from '../innerpage/Footer';

function PageLayout({ 
  children, 
  pageTitle, 
  className, 
  darkNavbar,
  customCss,
  customScript 
}) {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Hide loader after content is ready
    setIsLoading(false);

    // Reset loading state when route changes
    return () => {
      setIsLoading(true);
    };
  }, []);

  return (
    <>
      <Helmet>
        <title>{pageTitle}</title>
        {/* Base styles */}
        <link
          rel="stylesheet"
          type="text/css"
          href="/common/assets/css/common.css"
        />
        {/* Custom page style */}
        {customCss && (
          <link
            rel="stylesheet"
            type="text/css"
            href={customCss}
          />
        )}
        {/* Inner pages style */}
        {!customCss && (
          <link
            rel="stylesheet"
            type="text/css"
            href="/innerpages/assets/css/innerpages.css"
          />
        )}
        {/* Dark navbar style */}
        {darkNavbar && (
          <link
            rel="stylesheet"
            type="text/css"
            href="/innerpages/assets/css/dark-navbar.css"
          />
        )}
        {/* Scripts */}
        <script src="/common/assets/js/common_js.js"></script>
        {customScript && <script src={customScript}></script>}
        {!customScript && <script src="/innerpages/assets/js/innerpages.js"></script>}
      </Helmet>
      <body className={className}>
        {isLoading ? <Loader /> : (
          <div className="smooth-scroll-content" id="scrollsmoother-container">
            {children}
          </div>
        )}
      </body>
    </>
  );
}

export default PageLayout;
