import React, { useEffect, useRef } from 'react';
import PageLayout from '../components/layout/PageLayout';
import Menu from '../components/home1/Menu';
import Header from '../components/home1/Header';
import Experience from '../components/home1/Experience';
import Services from '../components/home1/Services';
import Process from '../components/home1/Process';
import Awards from '../components/home1/Awards';
import Projects from '../components/home1/Projects';
import Testimonials from '../components/home1/Testimonials';
import Blog from '../components/home1/Blog';
import Chat from '../components/home1/Chat';
import Footer from '../components/innerpage/Footer';

function Home1() {
  const firstRender = useRef(true);
  const isMobileDevice = useRef(window.innerWidth < 768);

  useEffect(() => {
    // Scroll to top when component mounts
    window.scrollTo(0, 0);
    
    // Force initialization of animations and smooth scrolling on all devices
    const initAnimations = () => {
      // Register plugins if not already registered
      if (window.gsap && !window.gsap._hasScrollTrigger) {
        window.gsap.registerPlugin(window.ScrollTrigger, window.ScrollSmoother);
      }
      
      // Kill any existing ScrollSmoother instance
      if (window.ScrollSmoother && window.ScrollSmoother.get()) {
        window.ScrollSmoother.get().kill();
      }
      
      // Create new instance with a short delay to ensure DOM is ready
      setTimeout(() => {
        if (window.ScrollSmoother) {
          // Create with mobile-friendly settings
          const smoother = window.ScrollSmoother.create({
            wrapper: '#scrollsmoother-container',
            content: '.smooth-scroll-content',
            smooth: isMobileDevice.current ? 1 : 1.5, // Less smooth on mobile for better performance
            effects: true,
            ignoreMobileResize: false, // Important for mobile
            normalizeScroll: true // Helps with mobile scrolling
          });
          
          // Refresh ScrollTrigger after ScrollSmoother is created
          if (window.ScrollTrigger) {
            window.ScrollTrigger.refresh();
          }
          
          // Fix mobile-specific animations
          if (isMobileDevice.current) {
            // Force all animations to play regardless of viewport size
            const allAnimationElements = document.querySelectorAll('[data-scroll]');
            allAnimationElements.forEach(el => {
              el.setAttribute('data-scroll', 'true');
              if (el.classList.contains('animated')) {
                // Force animation to be visible if it has the animated class
                el.style.opacity = '1';
                el.style.transform = 'none';
              }
            });
          }
        }
        
        // Initialize any custom home animations
        if (window.initHomeAnimations && typeof window.initHomeAnimations === 'function') {
          window.initHomeAnimations();
        }
      }, 100);
    };
    
    // If it's the first render or we're revisiting
    if (!firstRender.current) {
      initAnimations();
    } else {
      // On first load, let PageLayout handle initial animations
      firstRender.current = false;
      
      // Set up a resize observer to handle orientation changes
      const resizeObserver = new ResizeObserver((entries) => {
        const wasUsingMobile = isMobileDevice.current;
        isMobileDevice.current = window.innerWidth < 768;
        
        // If mobile status changed, reinit animations
        if (wasUsingMobile !== isMobileDevice.current) {
          initAnimations();
        }
      });
      
      resizeObserver.observe(document.documentElement);
      
      return () => {
        resizeObserver.disconnect();
      };
    }
    
    return () => {
      // Clean up animations when navigating away
      if (window.ScrollSmoother && window.ScrollSmoother.get()) {
        // Just mark that we need to refresh it when coming back
        firstRender.current = false;
      }
    };
  }, []);

  return (
    <PageLayout
      pageTitle="Groupe Strapex Maroc"
      className="home-style1"
      customCss="/home1/assets/css/home_1_style.css"
      customScript="/home1/assets/js/home_1_scripts.js"
      darkNavbar
    >
      <Menu />
      <Header />
      <main>
        <Experience />
        <Services />
        <Process />
        <Projects />
        <Testimonials />
        <Awards />
        <span id="#"></span>
        <Blog />
        <Chat />
      </main>
      <Footer />
    </PageLayout>
  );
}

export default Home1;
