import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import Loader from '../common/Loader';
import Menu from '../innerpage/Menu';
import Footer from '../innerpage/Footer';

function PageLayout({ 
  children, 
  pageTitle, 
  className, 
  darkNavbar,
  customCss,
  customScript 
}) {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Reset loading state on each page navigation
    setIsLoading(true);
    
    // Create a guaranteed timeout to hide loader
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1500);
    
    // Add an additional safety timeout to force hide the loader if it gets stuck
    const safetyTimer = setTimeout(() => {
      if (document.querySelector('.loading-wrapper')) {
        console.log('Loader safety timeout triggered');
        setIsLoading(false);
      }
    }, 5000);

    return () => {
      clearTimeout(timer);
      clearTimeout(safetyTimer);
    };
  }, [pageTitle]); // Re-run when page title changes (indicating navigation)

  return (
    <>
      <Helmet>
        <title>{pageTitle}</title>
        {/* Base styles */}
        <link
          rel="stylesheet"
          type="text/css"
          href="/common/assets/css/common.css"
        />
        {/* Footer styles */}
        <link
          rel="stylesheet"
          type="text/css"
          href="/common/assets/css/footer.css"
        />
        {/* Custom page style */}
        {customCss && (
          <link
            rel="stylesheet"
            type="text/css"
            href={customCss}
          />
        )}
        {/* Inner pages style */}
        {!customCss && (
          <link
            rel="stylesheet"
            type="text/css"
            href="/innerpages/assets/css/innerpages.css"
          />
        )}
        {/* Dark navbar style */}
        {darkNavbar && (
          <link
            rel="stylesheet"
            type="text/css"
            href="/innerpages/assets/css/dark-navbar.css"
          />
        )}
        {/* Scripts */}
        <script src="/common/assets/js/common_js.js"></script>
        {customScript && <script src={customScript}></script>}
        {!customScript && <script src="/innerpages/assets/js/innerpages.js"></script>}
      </Helmet>
      <body className={className}>
        {isLoading && <Loader />}
        <div className={`smooth-scroll-content ${isLoading ? 'content-loading' : ''}`} id="scrollsmoother-container">
          {children}
        </div>
      </body>
    </>
  );
}

export default PageLayout;
